let cart=[];function addToCart(e,t){let n=cart.find(t=>t.name===e);n?n.quantity+=1:cart.push({name:e,price:t,quantity:1}),updateCart()}function updateCart(){let e=document.getElementById("cart-items"),n=document.getElementById("cart-total"),s=document.getElementById("cart-count");e.innerHTML="";let t=0;cart.forEach(n=>{t+=n.price*n.quantity;let s=document.createElement("li");s.innerHTML=`<strong>${n.name}</strong> x${n.quantity} - $${n.price*n.quantity}`,e.appendChild(s)}),n.innerText=t,s.innerText=cart.length}function toggleCart(){let e=document.getElementById("cart-modal");e.style.display=e.style.display==="block"?"none":"block"}function sendOrder(){let t=document.getElementById("cart-notes").value,e="📋 *Pedido:*%0A";cart.forEach(t=>{e+=`🛍️ ${t.name} x${t.quantity} - $${t.price*t.quantity}%0A`}),e+=`%0A📝 *Notas:* ${t}`;let n=`https://wa.me/1234567890?text=${e}`;window.open(n,"_blank")}